import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../assets/managePrograms.css";

const ViewBugs = () => {
  const [bugs, setBugs] = useState([]);
  const [filteredBugs, setFilteredBugs] = useState([]);
  const [programs, setPrograms] = useState([]);
  const [users, setUsers] = useState([]);
  const [areas, setAreas] = useState([]);
  const [filtersVisible, setFiltersVisible] = useState(false);
  const [search, setSearch] = useState("");
  const [filters, setFilters] = useState({
    program: "",
    area: "",
    severity: "",
    priority: "",
    status: "",
    resolutionStatus: "",
    assignedTo: "",
    reportedBy: "",
    treatAsDeferred: false,
  });

  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem("user"));

  useEffect(() => {
    fetchBugs();
    fetchUsers();
    fetchPrograms();
  }, []);

  const fetchBugs = async () => {
    const token = localStorage.getItem("token");
    const res = await axios.get("http://localhost:8080/api/bugs", {
      headers: { Authorization: `Bearer ${token}` },
    });
    setBugs(res.data);
    setFilteredBugs(res.data);
  };

  const fetchUsers = async () => {
    const token = localStorage.getItem("token");
    const res = await axios.get("http://localhost:8080/api/users", {
      headers: { Authorization: `Bearer ${token}` },
    });
    setUsers(res.data);
  };

  const fetchPrograms = async () => {
    const token = localStorage.getItem("token");
    const res = await axios.get("http://localhost:8080/api/programs", {
      headers: { Authorization: `Bearer ${token}` },
    });
    setPrograms(res.data);
  };

  useEffect(() => {
    const filtered = bugs.filter((bug) => {
      return (
        bug.title.toLowerCase().includes(search.toLowerCase()) &&
        (filters.program ? bug.program?.programId == filters.program : true) &&
        (filters.area ? bug.functionalArea?.areaId == filters.area : true) &&
        (filters.severity ? bug.severity === filters.severity : true) &&
        (filters.priority ? bug.priority === filters.priority : true) &&
        (filters.status ? bug.status === filters.status : true) &&
        (filters.resolutionStatus ? bug.resolutionStatus === filters.resolutionStatus : true) &&
        (filters.assignedTo ? bug.assignedTo?.id == filters.assignedTo : true) &&
        (filters.reportedBy ? bug.reportedBy?.id == filters.reportedBy : true) &&
        (!filters.treatAsDeferred || bug.treatAsDeferred)
      );
    });
    setFilteredBugs(filtered);
  }, [search, filters, bugs]);

  const handleFilterChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFilters({
      ...filters,
      [name]: type === "checkbox" ? checked : value,
    });
  };

  return (
    <div style={{ padding: "30px", maxWidth: "1400px", margin: "0 auto" }}>
      <h2 style={{ color: "#fff", textAlign: "center", marginBottom: "20px" }}>Reported Bugs</h2>

      <input
        type="text"
        placeholder="Search by title..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        style={{ padding: "10px", width: "100%", marginBottom: "15px", fontSize: "16px" }}
      />

      <button
        onClick={() => setFiltersVisible(!filtersVisible)}
        style={{ marginBottom: "15px", padding: "8px 12px", backgroundColor: "#444", color: "white", border: "none", borderRadius: "5px" }}
      >
        {filtersVisible ? "Hide Filters" : "Show Filters"}
      </button>

      {filtersVisible && (
        <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fit, minmax(200px, 1fr))", gap: "15px", marginBottom: "20px" }}>
          <select name="program" onChange={handleFilterChange} value={filters.program}>
            <option value="">All Programs</option>
            {programs.map((p) => (
              <option key={p.programId} value={p.programId}>{p.name}</option>
            ))}
          </select>
          <select name="area" onChange={handleFilterChange} value={filters.area}>
            <option value="">All Areas</option>
            {bugs.map(b => b.functionalArea).filter(Boolean).map(a => (
              <option key={a.areaId} value={a.areaId}>{a.name}</option>
            ))}
          </select>
          <select name="severity" onChange={handleFilterChange} value={filters.severity}>
            <option value="">All Severities</option>
            <option value="LOW">Low</option>
            <option value="MEDIUM">Medium</option>
            <option value="HIGH">High</option>
            <option value="CRITICAL">Critical</option>
          </select>
          <select name="priority" onChange={handleFilterChange} value={filters.priority}>
            <option value="">All Priorities</option>
            <option value="Fix_immediately">Fix Immediately</option>
            <option value="Fix_ASAP">Fix ASAP</option>
            <option value="Fix_before_next_milestone">Fix Before Milestone</option>
            <option value="Fix_before_release">Fix Before Release</option>
            <option value="Fix_if_possible">Fix If Possible</option>
            <option value="Optional">Optional</option>
          </select>
          <select name="status" onChange={handleFilterChange} value={filters.status}>
            <option value="">All Statuses</option>
            <option value="OPEN">Open</option>
            <option value="RESOLVED">Resolved</option>
            <option value="CLOSED">Closed</option>
          </select>
          <select name="resolutionStatus" onChange={handleFilterChange} value={filters.resolutionStatus}>
            <option value="">All Resolutions</option>
            <option value="FIXED">Fixed</option>
            <option value="CANNOT_REPRODUCE">Cannot Reproduce</option>
            <option value="INCOMPLETE">Incomplete</option>
            <option value="WONT_FIX">Won't Fix</option>
            <option value="DUPLICATE">Duplicate</option>
          </select>
          <select name="assignedTo" onChange={handleFilterChange} value={filters.assignedTo}>
            <option value="">All Assignees</option>
            {users.map((u) => (
              <option key={u.id} value={u.id}>{u.name}</option>
            ))}
          </select>
          <select name="reportedBy" onChange={handleFilterChange} value={filters.reportedBy}>
            <option value="">All Reporters</option>
            {users.map((u) => (
              <option key={u.id} value={u.id}>{u.name}</option>
            ))}
          </select>
          <label style={{ color: "white" }}>
            <input
              type="checkbox"
              name="treatAsDeferred"
              checked={filters.treatAsDeferred}
              onChange={handleFilterChange}
              style={{ marginRight: "5px" }}
            />
            Deferred Only
          </label>
        </div>
      )}

      <div style={{ overflowX: "auto" }}>
        <table style={{ width: "100%", borderCollapse: "collapse", color: "white" }}>
          <thead style={{ backgroundColor: "#222" }}>
            <tr>
              <th>ID</th>
              <th>Title</th>
              <th>Program</th>
              <th>Severity</th>
              <th>Priority</th>
              <th>Status</th>
              <th>Deferred</th>
              <th>Reported By</th>
              {user.level === 3 && <th>Actions</th>}
            </tr>
          </thead>
          <tbody>
            {filteredBugs.length > 0 ? (
              filteredBugs.map((bug) => (
                <tr key={bug.bugId}>
                  <td>{bug.bugId}</td>
                  <td>{bug.title}</td>
                  <td>{bug.program?.name}</td>
                  <td>{bug.severity}</td>
                  <td>{bug.priority}</td>
                  <td>{bug.status}</td>
                  <td>{bug.treatAsDeferred ? "Yes" : "No"}</td>
                  <td>{bug.reportedBy?.name}</td>
                  {user.level === 3 && (
                    <td>
                      <button
                        style={{ background: "#2c8", border: "none", padding: "6px 12px", borderRadius: "6px" }}
                        onClick={() => navigate(`/edit-bug/${bug.bugId}`)}
                      >
                        Edit
                      </button>
                    </td>
                  )}
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="9" style={{ textAlign: "center", padding: "15px" }}>No bugs found.</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ViewBugs;
