import React, { useState, useEffect } from "react";
import axios from "axios";
import "../assets/form.css";

const ReportBug = () => {
  const [programs, setPrograms] = useState([]);
  const [functionalAreas, setFunctionalAreas] = useState([]);
  const [form, setForm] = useState({
    title: "",
    description: "",
    suggestedFix: "",
    severity: "LOW",
    reportType: "CODE_DEFECT",
    canReproduce: "Yes",
    priority: "Fix_immediately",
    programId: "",
    areaId: "",
    attachment: null,
    comments: "", // ✅ NEW
  });
  const [success, setSuccess] = useState("");
  const [error, setError] = useState("");

  useEffect(() => {
    fetchPrograms();
  }, []);

  const fetchPrograms = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get("http://localhost:8080/api/programs", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setPrograms(res.data);
    } catch (err) {
      setError("Failed to load programs.");
    }
  };

  const fetchFunctionalAreas = async (programId) => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get(
        `http://localhost:8080/api/functional-areas/${programId}`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setFunctionalAreas(res.data);
    } catch (err) {
      setError("Failed to load functional areas.");
    }
  };

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setForm({
      ...form,
      [name]: files ? files[0] : value,
    });

    if (name === "programId") {
      fetchFunctionalAreas(value);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");

    const token = localStorage.getItem("token");
    const user = JSON.parse(localStorage.getItem("user"));

    const payload = {
      title: form.title,
      description: form.description,
      suggestedFix: form.suggestedFix,
      severity: form.severity,
      reportType: form.reportType,
      canReproduce: form.canReproduce,
      priority: form.priority,
      version: "1.0",
      status: "OPEN",
      resolutionStatus: "PENDING",
      comments: form.comments, // ✅ ADDED HERE
      program: { programId: parseInt(form.programId) },
      functionalArea: { areaId: parseInt(form.areaId) },
      reportedBy: { id: user.id },
      dateReported: new Date().toISOString(),
    };

    try {
      const bugRes = await axios.post("http://localhost:8080/api/bugs/report", payload, {
        headers: { Authorization: `Bearer ${token}` },
      });

      const bugId = bugRes.data.bugId;

      if (form.attachment) {
        const formData = new FormData();
        formData.append("file", form.attachment);
        formData.append("bugId", bugId);
        formData.append("uploadedBy", user.id);

        await axios.post("http://localhost:8080/api/attachments/upload", formData, {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "multipart/form-data",
          },
        });
      }

      setSuccess("Bug reported successfully!");
      setForm({
        title: "",
        description: "",
        suggestedFix: "",
        severity: "LOW",
        reportType: "CODE_DEFECT",
        canReproduce: "Yes",
        priority: "Fix_immediately",
        programId: "",
        areaId: "",
        attachment: null,
        comments: "", // ✅ reset after submit
      });
      setFunctionalAreas([]);
    } catch (err) {
      console.error("Bug submission failed:", err);
      setError("Failed to report bug.");
    }
  };

  return (
    <div className="form-container">
      <h2>Report Bug</h2>
      {success && <p className="success-message">{success}</p>}
      {error && <p className="error-message">{error}</p>}

      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Bug Title"
          name="title"
          value={form.title}
          onChange={handleChange}
          required
        />

        <textarea
          placeholder="Bug Description"
          name="description"
          value={form.description}
          onChange={handleChange}
          required
        />

        <textarea
          placeholder="Suggested Fix (optional)"
          name="suggestedFix"
          value={form.suggestedFix}
          onChange={handleChange}
        />

        <textarea
          placeholder="Additional Comments (optional)"
          name="comments"
          value={form.comments}
          onChange={handleChange}
          rows="4"
        />

        <select name="programId" value={form.programId} onChange={handleChange} required>
          <option value="">Select Program</option>
          {programs.map((p) => (
            <option key={p.programId} value={p.programId}>
              {p.name} - v{p.version} (Release {p.releaseNum})
            </option>
          ))}
        </select>

        <select name="areaId" value={form.areaId} onChange={handleChange} required>
          <option value="">Select Functional Area</option>
          {functionalAreas.map((a) => (
            <option key={a.areaId} value={a.areaId}>
              {a.name}
            </option>
          ))}
        </select>

        <select name="severity" value={form.severity} onChange={handleChange}>
          <option value="LOW">Low</option>
          <option value="MEDIUM">Medium</option>
          <option value="HIGH">High</option>
          <option value="CRITICAL">Critical</option>
        </select>

        <select name="reportType" value={form.reportType} onChange={handleChange}>
          <option value="CODE_DEFECT">Code Defect</option>
          <option value="DOCUMENTATION">Documentation</option>
          <option value="ENHANCEMENT">Enhancement</option>
          <option value="OTHER">Other</option>
        </select>

        <select name="priority" value={form.priority} onChange={handleChange}>
          <option value="Fix_immediately">Fix Immediately</option>
          <option value="Fix_ASAP">Fix As Soon As Possible</option>
          <option value="Fix_before_next_milestone">Fix Before Next Milestone</option>
          <option value="Fix_before_release">Fix Before Release</option>
          <option value="Fix_if_possible">Fix If Possible</option>
          <option value="Optional">Optional</option>
        </select>

        <select name="canReproduce" value={form.canReproduce} onChange={handleChange}>
          <option value="Yes">Reproducible</option>
          <option value="No">Not Reproducible</option>
        </select>

        <input
          type="file"
          name="attachment"
          onChange={handleChange}
          accept="*"
        />

        <button type="submit">Submit Bug</button>
      </form>
    </div>
  );
};

export default ReportBug;
