import React, { useEffect, useState } from "react";
import axios from "axios";
import "../assets/managePrograms.css";

const ViewBugs = () => {
  const [bugs, setBugs] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    fetchBugs();
  }, []);

  const fetchBugs = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get("http://localhost:8080/api/bugs", {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (Array.isArray(res.data)) {
        setBugs(res.data);
      } else {
        setBugs([]);
        console.warn("Bug data was not an array");
      }
    } catch (err) {
      console.error("Error fetching bugs:", err);
      setError("Failed to fetch bugs.");
    }
  };

  return (
    <div
      className="fullscreen-bugs-container"
      style={{
        padding: "30px",
        maxWidth: "95%",
        margin: "0 auto",
        overflowX: "auto",
      }}
    >
      <h2 style={{ fontSize: "2.4rem", marginBottom: "30px", textAlign: "center" }}>
        Reported Bugs
      </h2>
      {error && <p className="error-message">{error}</p>}

      <div className="fullscreen-table-wrapper">
        <table className="fullscreen-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Title</th>
              <th>Program</th>
              <th>Area</th>
              <th>Severity</th>
              <th>Priority</th>
              <th>Status</th>
              <th>Resolution</th>
              <th>Reporter</th>
              <th>Assigned To</th>
              <th>Resolved By</th>
              <th>Comments</th>
              <th>Reported On</th>
            </tr>
          </thead>
          <tbody>
            {bugs.length > 0 ? (
              bugs.map((bug) => (
                <tr key={bug.bugId}>
                  <td>{bug.bugId}</td>
                  <td>{bug.title}</td>
                  <td>{bug.program?.name || "—"}</td>
                  <td>{bug.functionalArea?.name || "—"}</td>
                  <td>{bug.severity || "—"}</td>
                  <td>{bug.priority || "—"}</td>
                  <td>{bug.status || "Open"}</td>
                  <td>{bug.resolutionStatus || "—"}</td>
                  <td>{bug.reportedBy?.name || "—"}</td>
                  <td>{bug.assignedTo?.name || "—"}</td>
                  <td>{bug.resolvedBy?.name || "—"}</td>
                  <td>{bug.comments || "—"}</td>
                  <td>{bug.dateReported?.slice(0, 10) || "N/A"}</td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="13" style={{ textAlign: "center", padding: "20px" }}>
                  No bugs found.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ViewBugs;
