import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../assets/managePrograms.css";

const ViewBugs = () => {
  const [bugs, setBugs] = useState([]);
  const [error, setError] = useState("");
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem("user"));

  useEffect(() => {
    fetchBugs();
  }, []);

  const fetchBugs = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get("http://localhost:8080/api/bugs", {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (Array.isArray(res.data)) {
        setBugs(res.data);
      } else {
        setBugs([]);
        console.warn("Bug data was not an array");
      }
    } catch (err) {
      console.error("Error fetching bugs:", err);
      setError("Failed to fetch bugs.");
    }
  };

  return (
    <div
      style={{
        padding: "40px 0",
        display: "flex",
        justifyContent: "center",
      }}
    >
      <div
        style={{
          background: "rgba(255, 255, 255, 0.05)",
          padding: "30px",
          borderRadius: "14px",
          width: "100%",
          maxWidth: "1500px",
          boxShadow: "0px 12px 24px rgba(0,0,0,0.25)",
        }}
      >
        <h2
          style={{
            fontSize: "2.4rem",
            marginBottom: "25px",
            textAlign: "center",
            color: "#fff",
          }}
        >
          Reported Bugs
        </h2>

        {error && <p className="error-message">{error}</p>}

        <div
          style={{
            overflowX: "auto",
            borderRadius: "10px",
            background: "rgba(0,0,0,0.8)",
          }}
        >
          <table
            style={{
              width: "100%",
              borderCollapse: "collapse",
              color: "white",
              fontSize: "15px",
            }}
          >
            <thead>
              <tr style={{ background: "#000" }}>
                <th>ID</th>
                <th>Title</th>
                <th>Program</th>
                <th>Area</th>
                <th>Severity</th>
                <th>Priority</th>
                <th>Status</th>
                <th>Resolution</th>
                <th>Reporter</th>
                <th>Assigned To</th>
                <th>Resolved By</th>
                <th>Comments</th>
                <th>Reported On</th>
                <th>Deferred</th> {/* ✅ New column */}
                {user.level === 3 && <th>Actions</th>}
              </tr>
            </thead>
            <tbody>
              {bugs.length > 0 ? (
                bugs.map((bug) => (
                  <tr key={bug.bugId} style={{ backgroundColor: "rgba(255,255,255,0.04)" }}>
                    <td>{bug.bugId}</td>
                    <td>{bug.title}</td>
                    <td>{bug.program?.name || "—"}</td>
                    <td>{bug.functionalArea?.name || "—"}</td>
                    <td>{bug.severity || "—"}</td>
                    <td>{bug.priority || "—"}</td>
                    <td>{bug.status || "Open"}</td>
                    <td>{bug.resolutionStatus || "—"}</td>
                    <td>{bug.reportedBy?.name || "—"}</td>
                    <td>{bug.assignedTo?.name || "—"}</td>
                    <td>{bug.resolvedBy?.name || "—"}</td>
                    <td>{bug.comments || "—"}</td>
                    <td>{bug.dateReported?.slice(0, 10) || "N/A"}</td>
                    <td>{bug.treatAsDeferred ? "Yes" : "No"}</td> {/* ✅ New value */}
                    {user.level === 3 && (
                      <td>
                        <button
                          onClick={() =>
                            navigate(`/edit-bug/${bug.bugId}`, { state: { bug } })
                          }
                          style={{
                            padding: "6px 12px",
                            background: "#1e90ff",
                            color: "white",
                            border: "none",
                            borderRadius: "4px",
                            cursor: "pointer",
                          }}
                        >
                          Edit
                        </button>
                      </td>
                    )}
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan={user.level === 3 ? 15 : 14} style={{ textAlign: "center", padding: "20px" }}>
                    No bugs found.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default ViewBugs;
