import React, { useEffect, useState } from "react";
import axios from "axios";
import "../assets/managePrograms.css";

const ViewBugs = () => {
  const [bugs, setBugs] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    fetchBugs();
  }, []);

  const fetchBugs = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get("http://localhost:8080/api/bugs", {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (Array.isArray(res.data)) {
        setBugs(res.data);
        console.log("Fetched bugs:", res.data);
      } else {
        console.warn("Response was not an array", res.data);
        setBugs([]);
      }
    } catch (err) {
      console.error("Bug fetch error:", err);
      setError("Failed to fetch bugs.");
    }
  };

  return (
    <div className="manage-programs-container">
      <h2 style={{ fontSize: "2rem", marginBottom: "20px" }}>Reported Bugs</h2>
      {error && <p className="error-message">{error}</p>}

      <div className="scrollable-table-container" style={{ maxHeight: "600px", overflowY: "auto" }}>
        <table style={{ minWidth: "1200px" }}>
          <thead>
            <tr style={{ background: "black", color: "white" }}>
              <th>ID</th>
              <th>Title</th>
              <th>Program</th>
              <th>Functional Area</th>
              <th>Severity</th>
              <th>Priority</th>
              <th>Status</th>
              <th>Reporter</th>
              <th>Reported On</th>
            </tr>
          </thead>
          <tbody>
            {Array.isArray(bugs) && bugs.length > 0 ? (
              bugs.map((bug) => (
                <tr key={bug.bugId}>
                  <td>{bug.bugId}</td>
                  <td>{bug.title}</td>
                  <td>{bug.program?.name}</td>
                  <td>{bug.functionalArea?.name}</td>
                  <td>{bug.severity}</td>
                  <td>{bug.priority}</td>
                  <td>{bug.status || "Open"}</td>
                  <td>{bug.reportedBy?.name || "Unknown"}</td>
                  <td>{bug.dateReported?.slice(0, 10) || "N/A"}</td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="9" style={{ textAlign: "center" }}>
                  No bugs found.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ViewBugs;
