import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../assets/managePrograms.css";

const ViewBugs = () => {
  const [bugs, setBugs] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const [error, setError] = useState("");

  const [filters, setFilters] = useState({
    title: "",
    severity: "",
    priority: "",
    status: "",
    resolutionStatus: "",
    assignedTo: "",
    reportedBy: "",
    treatAsDeferred: "",
  });

  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem("user"));
  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!token) {
      setError("Unauthorized: No token found.");
      return;
    }
    fetchBugs();
    // eslint-disable-next-line
  }, []);

  const fetchBugs = async () => {
    try {
      const res = await axios.get("http://localhost:8080/api/bugs", {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (Array.isArray(res.data)) {
        setBugs(res.data);
        setFiltered(res.data);
      } else {
        setBugs([]);
        setFiltered([]);
      }
    } catch (err) {
      console.error("Error fetching bugs:", err);
      setError("Failed to fetch bugs.");
    }
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    const updated = { ...filters, [name]: value };
    setFilters(updated);

    const filteredData = bugs.filter((bug) => {
      return (
        (updated.title === "" || bug.title.toLowerCase().includes(updated.title.toLowerCase())) &&
        (updated.severity === "" || bug.severity === updated.severity) &&
        (updated.priority === "" || bug.priority === updated.priority) &&
        (updated.status === "" || bug.status === updated.status) &&
        (updated.resolutionStatus === "" || bug.resolutionStatus === updated.resolutionStatus) &&
        (updated.assignedTo === "" || bug.assignedTo?.name === updated.assignedTo) &&
        (updated.reportedBy === "" || bug.reportedBy?.name === updated.reportedBy) &&
        (updated.treatAsDeferred === "" ||
          String(bug.treatAsDeferred) === updated.treatAsDeferred)
      );
    });

    setFiltered(filteredData);
  };

  return (
    <div style={{ padding: "40px 0", display: "flex", justifyContent: "center" }}>
      <div style={{
        background: "rgba(255, 255, 255, 0.05)",
        padding: "30px",
        borderRadius: "14px",
        width: "100%",
        maxWidth: "1500px",
        boxShadow: "0px 12px 24px rgba(0,0,0,0.25)"
      }}>
        <h2 style={{ fontSize: "2.4rem", marginBottom: "25px", textAlign: "center", color: "#fff" }}>
          Reported Bugs
        </h2>

        {/* FILTERS */}
        <div style={{
          display: "flex",
          flexWrap: "wrap",
          gap: "15px",
          marginBottom: "20px",
          justifyContent: "center"
        }}>
          <input
            type="text"
            name="title"
            value={filters.title}
            onChange={handleFilterChange}
            placeholder="Search by Title"
            style={{ padding: "10px", minWidth: "200px", borderRadius: "6px" }}
          />
          <select name="severity" value={filters.severity} onChange={handleFilterChange}>
            <option value="">Severity</option>
            <option value="LOW">Low</option>
            <option value="MEDIUM">Medium</option>
            <option value="HIGH">High</option>
            <option value="CRITICAL">Critical</option>
          </select>
          <select name="priority" value={filters.priority} onChange={handleFilterChange}>
            <option value="">Priority</option>
            <option value="Fix_immediately">Fix Immediately</option>
            <option value="Fix_ASAP">Fix ASAP</option>
            <option value="Fix_before_next_milestone">Fix Before Milestone</option>
            <option value="Fix_before_release">Fix Before Release</option>
            <option value="Fix_if_possible">Fix If Possible</option>
            <option value="Optional">Optional</option>
          </select>
          <select name="status" value={filters.status} onChange={handleFilterChange}>
            <option value="">Status</option>
            <option value="OPEN">Open</option>
            <option value="RESOLVED">Resolved</option>
            <option value="CLOSED">Closed</option>
          </select>
          <select name="resolutionStatus" value={filters.resolutionStatus} onChange={handleFilterChange}>
            <option value="">Resolution</option>
            <option value="FIXED">Fixed</option>
            <option value="CANNOT_REPRODUCE">Cannot Reproduce</option>
            <option value="WONT_FIX">Won’t Fix</option>
            <option value="INCOMPLETE">Incomplete</option>
            <option value="DUPLICATE">Duplicate</option>
          </select>
          <select name="assignedTo" value={filters.assignedTo} onChange={handleFilterChange}>
            <option value="">Assigned To</option>
            {[...new Set(bugs.map(b => b.assignedTo?.name))].filter(Boolean).map(name => (
              <option key={name} value={name}>{name}</option>
            ))}
          </select>
          <select name="reportedBy" value={filters.reportedBy} onChange={handleFilterChange}>
            <option value="">Reported By</option>
            {[...new Set(bugs.map(b => b.reportedBy?.name))].filter(Boolean).map(name => (
              <option key={name} value={name}>{name}</option>
            ))}
          </select>
          <select name="treatAsDeferred" value={filters.treatAsDeferred} onChange={handleFilterChange}>
            <option value="">Deferred</option>
            <option value="true">Yes</option>
            <option value="false">No</option>
          </select>
        </div>

        {/* TABLE */}
        <div style={{
          overflowX: "auto",
          borderRadius: "10px",
          background: "rgba(0,0,0,0.8)"
        }}>
          <table style={{
            width: "100%",
            borderCollapse: "collapse",
            color: "white",
            fontSize: "15px",
          }}>
            <thead>
              <tr style={{ background: "#000" }}>
                <th>ID</th>
                <th>Title</th>
                <th>Program</th>
                <th>Area</th>
                <th>Severity</th>
                <th>Priority</th>
                <th>Status</th>
                <th>Resolution</th>
                <th>Reporter</th>
                <th>Assigned To</th>
                <th>Resolved By</th>
                <th>Comments</th>
                <th>Reported On</th>
                <th>Deferred</th>
                {user?.level === 3 && <th>Actions</th>}
              </tr>
            </thead>
            <tbody>
              {filtered.length > 0 ? (
                filtered.map((bug) => (
                  <tr key={bug.bugId} style={{ backgroundColor: "rgba(255,255,255,0.04)" }}>
                    <td>{bug.bugId}</td>
                    <td>{bug.title}</td>
                    <td>{bug.program?.name || "—"}</td>
                    <td>{bug.functionalArea?.name || "—"}</td>
                    <td>{bug.severity || "—"}</td>
                    <td>{bug.priority || "—"}</td>
                    <td>{bug.status || "Open"}</td>
                    <td>{bug.resolutionStatus || "—"}</td>
                    <td>{bug.reportedBy?.name || "—"}</td>
                    <td>{bug.assignedTo?.name || "—"}</td>
                    <td>{bug.resolvedBy?.name || "—"}</td>
                    <td>{bug.comments || "—"}</td>
                    <td>{bug.dateReported?.slice(0, 10) || "N/A"}</td>
                    <td>{bug.treatAsDeferred ? "Yes" : "No"}</td>
                    {user?.level === 3 && (
                      <td>
                        <button
                          onClick={() =>
                            navigate(`/edit-bug/${bug.bugId}`, {
                              state: { bug },
                            })
                          }
                          style={{
                            padding: "6px 12px",
                            background: "#1e90ff",
                            color: "white",
                            border: "none",
                            borderRadius: "4px",
                            cursor: "pointer"
                          }}
                        >
                          Edit
                        </button>
                      </td>
                    )}
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan={user?.level === 3 ? 15 : 14} style={{ textAlign: "center", padding: "20px" }}>
                    No bugs found.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default ViewBugs;
