import React, { useEffect, useState } from "react";
import axios from "axios";
import "../assets/managePrograms.css";

const ViewBugs = () => {
  const [bugs, setBugs] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    fetchBugs();
  }, []);

  const fetchBugs = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get("http://localhost:8080/api/bugs", {
        headers: { Authorization: `Bearer ${token}` },
      });

      // ✅ Normalize bugs whether response is array or object
      const bugList = Array.isArray(res.data)
        ? res.data
        : Array.isArray(res.data.bugs)
        ? res.data.bugs
        : [];

      console.log("Fetched bugs:", bugList);
      setBugs(bugList);
    } catch (err) {
      console.error("Bug fetch error:", err);
      setError("Failed to fetch bugs.");
    }
  };

  return (
    <div className="manage-programs-container">
      <h2>Reported Bugs</h2>
      {error && <p className="error-message">{error}</p>}
      <div className="table-container">
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Title</th>
              <th>Program</th>
              <th>Severity</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {Array.isArray(bugs) && bugs.length > 0 ? (
              bugs.map((bug) => (
                <tr key={bug.bugId}>
                  <td>{bug.bugId}</td>
                  <td>{bug.title}</td>
                  <td>{bug.program?.name}</td>
                  <td>{bug.severity}</td>
                  <td>{bug.status || "Open"}</td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="5" style={{ textAlign: "center" }}>
                  No bugs found.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ViewBugs;
