import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";
import "../assets/managePrograms.css";

const BugHistory = () => {
  const { id } = useParams(); // bugId
  const [history, setHistory] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    fetchHistory();
  }, []);

  const fetchHistory = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get(`http://localhost:8080/api/history/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setHistory(res.data);
    } catch (err) {
      setError("Failed to load history.");
      console.error(err);
    }
  };

  return (
    <div className="manage-programs-container">
      <h2>Bug History for ID #{id}</h2>
      <Link to="/view-bugs" style={{ color: "#00f" }}>← Back to Bug List</Link>
      {error && <p className="error-message">{error}</p>}
      <table className="fullscreen-table">
        <thead>
          <tr>
            <th>Date</th>
            <th>Updated By</th>
            <th>Description of Change</th>
          </tr>
        </thead>
        <tbody>
          {history.length > 0 ? (
            history.map((entry) => (
              <tr key={entry.historyId}>
                <td>{entry.dateUpdated?.slice(0, 19).replace("T", " ")}</td>
                <td>{entry.updatedBy?.name || "—"}</td>
                <td>{entry.changeDescription}</td>
              </tr>
            ))
          ) : (
            <tr><td colSpan="3">No history available.</td></tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default BugHistory;
