import React, { useEffect, useState } from "react";
import axios from "axios";
import "../assets/managePrograms.css";

const ViewBugs = () => {
  const [bugs, setBugs] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    fetchBugs();
  }, []);

const fetchBugs = async () => {
  try {
    const token = localStorage.getItem("token");
    const res = await axios.get("http://localhost:8080/api/bugs", {
      headers: { Authorization: `Bearer ${token}` },
    });

    const data = res.data;
    console.log("üì¶ API returned:", data);

    if (Array.isArray(data)) {
      setBugs(data);
    } else if (Array.isArray(data.bugs)) {
      setBugs(data.bugs);
    } else {
      setBugs([]);
    }
  } catch (err) {
    console.error("‚ùå Bug fetch error:", err);
    setError("Failed to fetch bugs.");
  }
};


  return (
    <div className="fullscreen-bugs-container">
      <h2>Reported Bugs</h2>
      {error && <p className="error-message">{error}</p>}

      <div className="fullscreen-table-wrapper">
        <table className="fullscreen-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Title</th>
              <th>Program</th>
              <th>Functional Area</th>
              <th>Severity</th>
              <th>Priority</th>
              <th>Status</th>
              <th>Reporter</th>
            </tr>
          </thead>
          <tbody>
            {Array.isArray(bugs) && bugs.length > 0 ? (
              bugs.map((bug) => (
                <tr key={bug.bugId}>
                  <td>{bug.bugId}</td>
                  <td>{bug.title}</td>
                  <td>{bug.program?.name}</td>
                  <td>{bug.functionalArea?.name || "-"}</td>
                  <td>{bug.severity}</td>
                  <td>{bug.priority}</td>
                  <td>{bug.status || "Open"}</td>
                  <td>{bug.reportedBy?.name || "-"}</td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="8" style={{ textAlign: "center" }}>
                  No bugs found.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ViewBugs;
